import React, {useState} from 'react';
import PropTypes from 'prop-types';
import '../App.css';

let def = -1;
export default function Alert(props) {
    if (props.message != null){
        if (props.message.id != def){
            def = props.message.id;
            let superdiv = document.getElementById("supdiv");
            let alertelement = document.createElement('div');
            alertelement.className = "alert alert-success d-flex align-items-center";
            alertelement.role = "alert";
            alertelement.style.width = "500px";
            let subelement = document.createElement('div');
            subelement.innerHTML = props.message.message;
            alertelement.appendChild(subelement);
            superdiv.appendChild(alertelement);
        }  
    }      
    return (
        <>
        {             
            props.message &&
            (<div className="allert" id="supdiv">
                {/* {
                    <div class="alert alert-success d-flex align-items-center" role="alert" style={{width: 500 + 'px'}}>
                        <div>
                            {props.message.message}
                        </div>  
                    </div>               
                } */}
            </div>)
        }
        </>
    );
}
