import React, {useState} from 'react'
import PropTypes from 'prop-types'
//useState is called "hook"

export default function TextForm(props) {
    const [text, setText] = useState("2"); //this is main - initialize a variable and function and set initial state
    //to update text, you have to mandatory call setText

  return (
    <div>
        <div className="mb-3">
            <label htmlFor="myText" className="form-label">{props.heading} - {text}</label>
            <textarea className="form-control" id="myBox" rows="9" placeholder="name@example.com"/>
        </div>
    </div>
  )
}

TextForm.propTypes = { title : PropTypes.string.isRequired,
                     }
TextForm.defaultProps = {
  heading : "Enter Text Here",
}
