import React, {useState} from 'react'
//useState is called "hook"

export default function TextForm(props) {
    const [text, setText] = useState("Enter Text Here"); //this is main - initialize a variable and function and set initial state
    //to update text, you have to mandatory call setText

  return (
    <div>
        <div className="mb-3">
            <label htmlFor="myText" className="form-label">props.heading</label>
            <input type="email" className="form-control" id="exampleFormControlInput1" placeholder="name@example.com"/>
        </div>
    </div>
  )
}
