import React, {useState} from 'react'
import PropTypes from 'prop-types'
//useState is called "hook"

export default function TextForm(props) {
    const [text, setText] = useState("2"); //this is main - initialize a variable and function and set initial state
    //to update text, you have to mandatory call setText    
    const handleUpClick = ()=>{
        let newText = text.toUpperCase();
        setText(newText);        
    }
    const handleOnChange = (event)=>{
        setText(event.target.value);
    }
  return (
    <div>
        <h1>Text Area</h1>
        <div className="mb-3">
            <label htmlFor="myText" className="form-label">{props.heading}</label>
            <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="9" placeholder="name@example.com"/>
        </div>
        <button className="btn btn-primary" onClick={handleUpClick}>Convert to Upper Case</button>            
        <h2>Text count</h2>
        <div>The word count is {text.split(" ").length} and the number of characters is {text.length}</div>
        <div>Time taken to read : {0.008 * text.split(" ").length} minutes</div>
        <h2>Preview</h2>
        <span>{Text}</span>
    </div>
  )
}

TextForm.propTypes = { title : PropTypes.string.isRequired,
                     }
TextForm.defaultProps = {
  heading : "Enter Text Here",
}
