import logo from './logo.svg';
import './App.css';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
import About from './components/About';
import React, {useState} from 'react';
import {
  BrowserRouter as Router,
  Routes,  
  Route,
  Link,
} from "react-router-dom";


function App() {
  const [mode, setMode] = useState("light");
  const changeMode = () => {
    if (mode == "dark"){
      setMode("light");
      document.body.style.backgroundColor = "#fff";
    }
    else{
      setMode("dark");
      document.body.style.backgroundColor = "#212529";
    }    
  };
  return (    
    <Router>
      <div>
        <Navbar title="Text Analytics" changeMode={changeMode} mode={mode}/>     
        {/* <div className="container my-3">
          <About mode={mode}/>
        </div> */}
        <div class="alert alert-success d-flex align-items-center" role="alert">
          <svg class="bi flex-shrink-0 me-2" role="img" aria-label="Success:"><use xlinkHref="#check-circle-fill"/></svg>
          <div>
            An example success alert with an icon
          </div>
        </div>
        <Routes> 
          <Route exact path='/' element={< TextForm heading="Enter the Text to analyse below" mode={mode}/>}></Route> 
          <Route exact path='/about' element={< About mode={mode}/>}></Route> 
        </Routes>
        {/* <div className="container my-3">                     
          <TextForm heading="Enter the Text to analyse below" mode={mode}/>
        </div> */}
      </div>
    </Router>
  );
}

export default App;
