import React, {useState} from 'react';
import PropTypes from 'prop-types';
import '../App.css';

let def = -1;
export default function Alert(props) {
    if (props.message != null){
        if (props.message.id != def){
            def = props.message.id;
            let superdiv = null;
            if (document.getElementById("supdiv") != null){
                superdiv = document.getElementById("supdiv");
            }
            else{
                superdiv = document.createElement("div");
                superdiv.className = "allert";
                superdiv.id = "supdiv";    
            }
            let alertelement = createNewElement();
            let subelement = document.createElement('div');
            subelement.innerHTML = props.message.message;
            alertelement.appendChild(subelement);
            let otheralerts = superdiv.getElementsByClassName('alert');            
            // for (let j=0; j<otheralerts.length; j++){
            //     superdiv.removeChild(otheralerts[j]);
            // }            
            superdiv.appendChild(alertelement);
            alertelement.style.position = "absolute";
            alertelement.style.top = 0;
            otheralerts[1].style.marginTop = 50;
            for (let j=1; j<otheralerts.length; j++){
                otheralerts[j].style.position = "relative";
            }  
            // alertelement.style.position = "absolute";
            // alertelement.style.top = 0;            
        }  
    }
    function createNewElement(){
        let alertelement = document.createElement('div');
            alertelement.className = "alert alert-success d-flex align-items-center";
            alertelement.role = "alert";
            alertelement.style.width = "500px";
            return alertelement;
    }      
    return (
        <>
        {             
            <div className="allert" id="supdiv">

            </div>
        }
        </>
    );
}
